import { Component } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { Writeup, WriteupService } from "src/app/services/writeup.service";

@Component({
  selector: "app-writeup-edit",
  template: `
    <div class="container" style="padding-top:65px">
      <!-- <h3>{{ writeup.title }}</h3> -->

      <div class="mb-3">
        <label class="form-label">Title</label>
        <input type="text" class="form-control" placeholder="Title" [(ngModel)]="writeup.title" />
      </div>

      <div class="mb-3">
        <label class="form-label">URL</label>
        <input type="text" class="form-control" placeholder="URL" [(ngModel)]="writeup.url" />
      </div>

      <div class="mb-3">
        <label class="form-label">CTF Name</label>
        <input type="text" class="form-control" placeholder="CTF Name" [(ngModel)]="writeup.ctfName" />
      </div>

        <button class="btn btn-primary">Save</button>
      </div>
    </div>
  `,
  styles: [],
})
export class WriteupEditComponent {
  writeup: Writeup = {} as Writeup;

  constructor(route: ActivatedRoute, writeupService: WriteupService) {
    route.params.subscribe((params) => {
      if (params["id"]) {
        writeupService.getWriteup(params["id"]).subscribe((writeup) => {
          this.writeup = writeup;
        });
      }
    });
  }
}
