import { Location } from "@angular/common";
import { Component } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { CTF, CTFService, Challenge } from "src/app/services/ctf.service";
import { User, UserService } from "src/app/services/user.service";
import { Writeup, WriteupService } from "src/app/services/writeup.service";

@Component({
  selector: "app-ctf-edit",
  template: `<div class="container" style="padding-top: 65px">
    <div class="mb-3">
      <label class="form-label">CTF Name</label>
      <input type="text" class="form-control" placeholder="CTF Name" [(ngModel)]="ctf.name" />
    </div>

    <div class="input-group mb-3">
      <input type="text" class="form-control" placeholder="Categories" [(ngModel)]="newCategory" />
      <button class="btn btn-secondary" (click)="addCategory(newCategory)">Add Category</button>
    </div>
    <!-- <div *ngFor="let category of ctf.categories" class="badge bg-secondary">{{ category }} <button tyle="button" class="btn-close" (click)="removeCategory(category)"></button></div> -->

    <div *ngFor="let category of ctf.categories">
      <h3>{{ category }}</h3>
      <table class="table table-bordered">
        <thead>
          <tr>
            <th>Challenge Name</th>
            <th>Solves</th>
            <th>Writeups</th>
          </tr>
        </thead>

        <tbody>
          <tr *ngFor="let challenge of filterChallenges(category)">
            <td>{{ challenge.name }}</td>
            <td>{{ challenge.solves }}</td>
            <td>
              <ul>
                <li *ngFor="let writeup of filterWriteups(challenge.id)">
                  <a href="{{ writeup.url }}" target="_blank">({{ writeup.votes || 0 }}) {{ writeup.url }}</a>
                </li>
              </ul>
              <div class="input-group mb-3">
                <input type="text" class="form-control" placeholder="Writeup URL" [(ngModel)]="newCategoryWriteupURL[category]" />
                <button class="btn btn-secondary btn-sm" (click)="saveWriteup(challenge, newCategoryWriteupURL[category])">Add Writeup</button>
              </div>
            </td>
          </tr>

          <tr>
            <td>
              <input type="text" class="form-control" placeholder="Challenge Name" [(ngModel)]="newCategoryChallenge[category].name" />
            </td>
            <td>
              <input type="number" class="form-control" placeholder="Solves" [(ngModel)]="newCategoryChallenge[category].solves" />
            </td>

            <td>
              <button class="btn btn-secondary btn-sm" (click)="saveChallenge(category)">Save New Challenge</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>

    <button (click)="save()" class="btn btn-primary">Save</button>
  </div>`,
  styles: [],
})
export class CtfEditComponent {
  ctf: CTF = {} as CTF;
  me: User = {} as User;
  newCategory = "";
  newCategoryChallenge: { [key: string]: Challenge } = {};
  newCategoryWriteupURL: { [key: string]: string } = {};

  challenges: Challenge[] = [];
  writeups: Writeup[] = [];

  constructor(private route: ActivatedRoute, private ctfService: CTFService, private location: Location, private userService: UserService, private writeupService: WriteupService) {}

  ngOnInit() {
    this.userService.getMe().subscribe((me) => {
      this.me = me;

      this.route.params.subscribe((params) => {
        if (params["id"]) {
          this.ctfService.getCTF(params["id"]).subscribe((ctf) => {
            this.ctf = ctf;

            for (let category of this.ctf.categories) {
              this.newCategoryChallenge[category] = { ctfID: this.ctf.id, category: category } as Challenge;
            }
          });

          this.ctfService.getChallenges(params["id"]).subscribe((challenges) => {
            this.challenges = challenges;
          });

          this.writeupService.getWriteupsForCTF(params["id"]).subscribe((writeups) => {
            this.writeups = writeups;
          });
        } else {
          this.ctf.submitterID = this.me.id;
        }
      });
    });
  }

  newCTF() {
    this.location.go("/ctfs/new");
  }

  save() {
    if (this.ctf.id) {
      this.ctfService.updateCTF(this.ctf).subscribe(() => {
        this.location.go(`/ctfs/${this.ctf.id}/edit`);
      });
    } else {
      this.ctfService.newCTF(this.ctf).subscribe((ctf) => {
        this.ctf = ctf;
        this.location.go(`/ctfs/${this.ctf.id}/edit`);
      });
    }
  }

  removeCategory(category: string) {
    if (!this.ctf.categories) {
      this.ctf.categories = [];
    }
    this.ctf.categories = this.ctf.categories.filter((t) => t !== category);
  }

  addCategory(category: string) {
    if (!this.ctf.categories) {
      this.ctf.categories = [];
    }
    this.ctf.categories.push(category);
    this.newCategory = "";
    this.newCategoryChallenge[category] = { ctfID: this.ctf.id, category: category } as Challenge;
  }

  saveChallenge(category: string) {
    this.ctfService.newChallenge(this.newCategoryChallenge[category]).subscribe((challenge) => {
      this.challenges.push(challenge);
    });
  }

  deleteChallenge() {}

  filterChallenges(category: string): Challenge[] {
    if (this.challenges == null) {
      return [];
    }
    return this.challenges.filter((challenge) => challenge.category === category);
  }

  filterWriteups(challengeID: string): Writeup[] {
    if (this.writeups == null) {
      return [];
    }

    return this.writeups.filter((writeup) => writeup.challengeID === challengeID);
  }

  saveWriteup(challenge: Challenge, url: string) {
    this.writeupService
      .newWriteup({
        challengeID: challenge.id,
        challengeName: challenge.name,
        challengeCategory: challenge.category,
        ctfName: this.ctf.name,
        url: url,
        ctfID: this.ctf.id,
        submitterID: this.me.id,
      } as Writeup)
      .subscribe((writeup) => {
        this.writeups.push(writeup);
      });
  }
}
