import { Component } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { Writeup, WriteupService } from "src/app/services/writeup.service";

@Component({
  selector: "app-writeup-edit",
  template: `
    <div class="container" style="padding-top:65px">
      <!-- <h3>{{ writeup.title }}</h3> -->

      <div class="mb-3">
        <label class="form-label">Title</label>
        <input type="text" class="form-control" placeholder="Title" [(ngModel)]="writeup.title" />
      </div>

      <div class="mb-3">
        <label class="form-label">URL</label>
        <input type="text" class="form-control" placeholder="URL" [(ngModel)]="writeup.url" />
      </div>

      <div class="mb-3">
        <label class="form-label">CTF Name</label>
        <input type="text" class="form-control" placeholder="CTF Name" [(ngModel)]="writeup.ctfName" />
      </div>

      <div class="mb-3">
        <label class="form-label">Challenge Category</label>
        <input type="text" class="form-control" placeholder="Challenge Category" [(ngModel)]="writeup.challengeCategory" />
      </div>

      <div class="mb-3">
        <label class="form-label">Solves</label>
        <input type="text" class="form-control" placeholder="Solves" [(ngModel)]="writeup.solves" />
        <div class="form-text">Optional, it helps others gauge difficulty.</div>
      </div>

      <div class="mb-3">
        <label class="form-label">Difficulty</label>
        <input type="text" class="form-control" placeholder="Difficulty" [(ngModel)]="writeup.difficulty" />
        <div class="form-text">Optional, it helps others gauge difficulty.</div>
      </div>

      <div class="mb-3">
        <label class="form-label">Tags</label>
        <input type="text" class="form-control" placeholder="Tags" [(ngModel)]="newTag" />
        <button class="btn btn-secondary" (click)="writeup.tags.push(newTag)">Add Tag</button>

        <div *ngFor="let tag of writeup.tags" class="badge bg-secondary">{{ tag }} <button tyle="button" class="btn-close" (click)="removeTag(tag)"></button></div>

        <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
      </div>

      <button class="btn btn-primary">Save</button>
    </div>
  `,
  styles: [],
})
export class WriteupEditComponent {
  writeup: Writeup = {} as Writeup;
  newTag = "";

  constructor(route: ActivatedRoute, writeupService: WriteupService) {
    route.params.subscribe((params) => {
      if (params["id"]) {
        writeupService.getWriteup(params["id"]).subscribe((writeup) => {
          this.writeup = writeup;
        });
      }
    });
  }

  removeTag(tag: string) {
    this.writeup.tags = this.writeup.tags.filter((t) => t !== tag);
  }
}
