import { Location } from "@angular/common";
import { Component } from "@angular/core";
import { CommentService, Comment } from "src/app/services/comment.service";
import { User } from "src/app/services/user.service";
import { Vote, VoteService } from "src/app/services/vote.service";
import { Writeup, WriteupService } from "src/app/services/writeup.service";

@Component({
  selector: "app-top",
  template: ` <div style="padding-top: 65px" class="container">
    <!-- // drop down button select day, week, month, all time -->
    <!-- <div class="btn-group float-end">
      <button type="button" class="btn btn-secondary dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">Week</button>
      <ul class="dropdown-menu dropdown-menu-end">
        <li><a class="dropdown-item" href="#">Newest</a></li>
        <li><a class="dropdown-item" href="#">Day</a></li>
        <li><a class="dropdown-item" href="#">Week</a></li>
        <li><a class="dropdown-item" href="#">Month</a></li>
        <li><a class="dropdown-item" href="#">All Time</a></li>
      </ul>
    </div> -->
    <div class="clearfix"></div>
    <div *ngFor="let writeup of sortedWriteups()" class="card">
      <div class="card-body">
        <div class="row">
          <div class="col-md-1">
            <i *ngIf="!hasVoted(writeup)" (click)="addVote(writeup)" class="bi bi-caret-up" style="font-size: 2rem;   vertical-align: 0em;"></i>
            <i *ngIf="hasVoted(writeup)" (click)="removeVote(getVote(writeup)!, writeup)" class="bi bi-caret-up text-success"></i>

            {{ writeup.voteCount }}
          </div>

          <div class="col-md-1">{{ writeup.ts | dateAgo }}</div>

          <div class="col-md-2">
            <a [routerLink]="['/ctfs', writeup.ctfID, 'edit']"> {{ writeup.ctfName }}</a>
          </div>

          <div class="col-md-4">
            <a href="{{ writeup.url }}" rel="noopener noreferrer" target="_blank"> {{ writeup.challengeCategory }} / {{ writeup.challengeName }}</a>
            <span class="text-muted" style="padding-left: 5px">({{ getDomain(writeup.url) }})</span>
          </div>

          <div class="col-md-2">
            <div *ngFor="let tag of writeup.tags" class="badge bg-secondary">{{ tag }}</div>
          </div>

          <div class="col-md-1" (click)="expandWriteup(writeup)">{{ writeup.commentCount || 0 }} <i class="bi bi-chat"></i></div>

          <div class="col-md-1">
            <button *ngIf="!writeup.isExpanded" type="button" class="btn btn-primary btn-sm float-end" (click)="expandWriteup(writeup)">Expand</button>
            <button *ngIf="writeup.isExpanded" type="button" class="btn btn-primary btn-sm float-end" (click)="writeup.isExpanded = false">Hide</button>

            <button *ngIf="me.isAdmin || writeup.submitterID == me.id" type="button" class="btn btn-warning btn-sm float-end" [routerLink]="['/writeups', writeup.id, 'edit']">Edit</button>
          </div>
        </div>

        <div *ngIf="writeup.isExpanded">
          <div *ngFor="let comment of writeup.comments">
            <hr />
            <p class="text-muted">{{ comment.username || "Anonymous" }} - {{ comment.ts | dateAgo }}</p>
            <p>{{ comment.body }}</p>
          </div>

          <div *ngIf="!!me.id">
            <textarea [(ngModel)]="writeup.newComment.body" class="form-control"></textarea>
            <button (click)="addComment(writeup)" class="btn btn-primary">Add Comment</button>
          </div>
        </div>
      </div>
    </div>
  </div>`,
  styles: [],
})
export class TopComponent {
  me: User = {} as User;
  writeups: Writeup[] = [];
  votes: Vote[] = [];

  constructor(private writeupService: WriteupService, private location: Location, private voteService: VoteService, private commentService: CommentService) {}

  ngOnInit(): void {
    this.writeupService.getWriteups().subscribe((writeups) => {
      this.writeups = writeups;

      this.writeups.forEach((writeup) => {
        writeup.newComment = {} as Comment;
        writeup.newComment.body = "";
        writeup.isExpanded = false;
        writeup.comments = writeup.comments || [];
      });
    });

    this.voteService.getVotes().subscribe((votes) => {
      this.votes = votes || [];
    });
  }

  hasVoted(writeup: Writeup): boolean {
    if (!this.votes) {
    }
    return !!this.votes.find((v) => v.writeupID === writeup.id);
  }

  getVote(writeup: Writeup): Vote | undefined {
    return this.votes.find((v) => v.writeupID === writeup.id);
  }

  addVote(writeup: Writeup): void {
    const vote: Vote = {
      writeupID: writeup.id,
      userID: this.me.id,
    } as Vote;

    this.voteService.newVote(vote).subscribe((v) => {
      this.votes.push(v);
      writeup.voteCount++;
    });
  }

  removeVote(vote: Vote, writeup: Writeup): void {
    console.log("remove vote", vote);
    this.voteService.deleteVote(vote).subscribe((_) => {
      this.votes = this.votes.filter((v) => vote.id !== v.id);
      writeup.voteCount--;
    });
  }

  getDomain(u: string): string {
    return new URL(u).hostname;
  }

  sortedWriteups(): Writeup[] {
    return this.writeups.sort((a, b) => b.voteCount - a.voteCount);
  }

  expandWriteup(writeup: Writeup): void {
    writeup.isExpanded = true;

    this.commentService.getCommentsForWriteup(writeup.id).subscribe((comments) => {
      writeup.comments = comments;
    });
  }

  addComment(writeup: Writeup): void {
    writeup.newComment.writeupID = writeup.id;
    writeup.newComment.userID = this.me.id;

    this.commentService.newComment(writeup.newComment).subscribe((comment) => {
      writeup.comments.push(comment);
      writeup.commentCount++;
      writeup.newComment.body = "";
    });
  }
}
