import { Location } from "@angular/common";
import { Component } from "@angular/core";
import { User } from "src/app/services/user.service";
import { Writeup, WriteupService } from "src/app/services/writeup.service";

@Component({
  selector: "app-home",
  template: ` <div style="padding-top: 60px" class="container">
    {{ writeups }}

    <!-- // drop down button select day, week, month, all time -->
    <div class="btn-group float-right">
      <button type="button" class="btn btn-secondary dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">Week</button>
      <ul class="dropdown-menu dropdown-menu-end">
        <li><a class="dropdown-item" href="#">Day</a></li>
        <li><a class="dropdown-item" href="#">Week</a></li>
        <li><a class="dropdown-item" href="#">Month</a></li>
        <li><a class="dropdown-item" href="#">All Time</a></li>
      </ul>
    </div>

    <div *ngFor="let writeup of writeups" class="card">
      <div class="card-body">
        <div class="row">
          <div class="col-md-1"><i class="bi bi-arrow-up-square"></i> 0</div>

          <div class="col-md-6">
            <a href="{{ writeup.url }}" target="_blank"> {{ writeup.title }}</a>
          </div>

          <div class="col-md-3">{{ writeup.ctfName }} - {{ writeup.challengeCategory }}</div>

          <div class="col-md-2">
            <button type="button" class="btn btn-warning btn-sm" [routerLink]="['/writeup', writeup.id, 'edit']">Edit</button>
          </div>
        </div>
      </div>
    </div>
  </div>`,
  styles: [],
})
export class HomeComponent {
  me: User = {} as User;
  writeups: Writeup[] = [];

  constructor(private writeupService: WriteupService, private location: Location) {}

  ngOnInit(): void {
    this.writeupService.getWriteups().subscribe((writeups) => {
      this.writeups = writeups;
    });
  }

  newWriteup(): void {
    // this.location.go(`/writeup/${writeup.id}/edit`);
  }
}
