import { Location } from "@angular/common";
import { Component } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { CTF, CTFService } from "src/app/services/ctf.service";
import { User, UserService } from "src/app/services/user.service";

@Component({
  selector: "app-ctf-edit",
  template: `<div class="container" style="padding-top: 65px">
    <div class="mb-3">
      <label class="form-label">CTF Name</label>
      <input type="text" class="form-control" placeholder="CTF Name" [(ngModel)]="writeup.ctfName" />
      <div class="form-text">Try to use an existing name, if it doesn't exist it will be created.</div>
    </div>

    <div class="mb-3">
      <label class="form-label">Challenge Name</label>
      <input type="text" class="form-control" placeholder="CTF Name" [(ngModel)]="writeup.challengeName" />
      <div class="form-text">Try to use an existing name, if it doesn't exist it will be created.</div>
    </div>

    <div class="mb-3">
      <label class="form-label">Challenge Category</label>
      <input type="text" class="form-control" placeholder="Challenge Category" [(ngModel)]="writeup.challengeCategory" />
      <div class="form-text">"Pwn", "Web", "Crypto", etc.</div>
    </div>

    <div class="mb-3">
      <label class="form-label">Difficulty</label>
      <input type="text" class="form-control" placeholder="Difficulty" [(ngModel)]="writeup.difficulty" />
      <div class="form-text">Optional, it helps others gauge difficulty. "Easy", "Medium", "Hard". Feel free to leave empty if unsure.</div>
    </div>

    {{ ctf }}
  </div>`,
  styles: [],
})
export class CtfEditComponent {
  ctf: CTF = {} as CTF;
  me: User = {} as User;

  constructor(private route: ActivatedRoute, private ctfService: CTFService, private location: Location, private userService: UserService) {}

  ngOnInit() {
    this.userService.getMe().subscribe((me) => {
      this.me = me;

      this.route.params.subscribe((params) => {
        if (params["id"]) {
          this.ctfService.getCTF(params["id"]).subscribe((ctf) => {
            this.ctf = ctf;
          });
        } else {
          this.ctf.submitterID = this.me.id;
        }
      });
    });
  }

  newCTF() {
    this.location.go("/ctfs/new");
  }
}
