export class CtfEditComponent {
  constructor(route, ctfService, location, userService, writeupService) {
    this.route = route;
    this.ctfService = ctfService;
    this.location = location;
    this.userService = userService;
    this.writeupService = writeupService;
    this.ctf = {};
    this.me = {};
    this.newCategory = "";
    this.newCategoryChallenge = {};
    this.newChallengeWriteupURL = {};
    this.challenges = [];
    this.writeups = [];
  }
